<?phpnamespace App\Http\Controllers;use App\Sede;use Illuminate\Http\Request;use Illuminate\Support\Facades\Hash;class SedeController extends Controller {    /**     * Display a listing of the resource.     *     * @return \Illuminate\Http\Response     */    public function __construct() {        $this->middleware('auth');    }    public function index() {                $sedes = Sede::all();        return view('sede.create', compact('sedes'));    }    /**     * Show the form for creating a new resource.     *     * @return \Illuminate\Http\Response     */    public function postCreate(Request $data) {        $name = $data->input('nombre');        if ($name) {            $sede = new Sede;            $sede->nombre = $name;                        $sede->save();        }        return redirect('/sede');    }     public function postEdit(Request $data) {        $id = $data->input('id');         $name= $data->input('nombre');                  $sede = Sede::find($id);         $sede->nombre =$name;         $sede->save();                return redirect('/sede');    }       public function getDelete( $id){              $sedeverificar = Sede::find($id);       $tieneprestamos = \App\PrestamoProducto::where('id_sede', $id)->where('estado', 'Prestado')->get();       if(count($tieneprestamos)){          echo 'No puedes eliminar esta sede porque tiene prÃ©stamo de producto sin devolver';         }       else{       $sede = Sede::find($id)->delete();        return redirect('/sede');                  }                                        }     public function getEdit($id){              $sede = Sede::find($id);                  return view('sede.edit', compact('sede'));            }}